import React, { useState } from "react";
import * as verify from "../api/verify";

function SubscribeForm(props) {
  const [phoneNumber, setPhoneNumber] = useState("0");
  const [zipCode, setZipCode] = useState(94131);

  const onPhoneNumberChange = (event) => {
    setPhoneNumber(event.target.value);
  };

  const onZipCodeChange = (event) => {
    setZipCode(event.target.value);
  };

  const onSubmit = () => {
    verify.getCode(phoneNumber);
    props.setPhoneNumber(phoneNumber);
    props.setZipCode(zipCode);
    props.setShowOpt(true);
  };

  return (
    <React.Fragment>
      <label>
        Phone number:
        <input type="tel" name="Phone number" onChange={onPhoneNumberChange} />
      </label>

      <label>
        Zip code:
        <input type="number" name="Zip code" onChange={onZipCodeChange} />
      </label>
      <input type="submit" value="Submit" onClick={() => onSubmit()} />
    </React.Fragment>
  );
}

export default SubscribeForm;
